import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navigation = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const getNavLinks = () => {
    if (!isAuthenticated) {
      return (
        <>
          <Nav.Link as={Link} to="/login">Login</Nav.Link>
          <Nav.Link as={Link} to="/register">Register</Nav.Link>
        </>
      );
    }

    const links = [
      { to: "/", text: "Home" },
      { to: "/profile", text: "Profile" }
    ];

    // Add role-specific links
    if (user?.role === 'ADMIN') {
      links.push(
        { to: "/admin", text: "Admin Dashboard" },
        { to: "/memberships", text: "Memberships" },
        { to: "/equipment", text: "Equipment" },
        { to: "/training-sessions", text: "Training Sessions" },
        { to: "/gym-classes", text: "Gym Classes" },
        { to: "/class-registrations", text: "Class Registrations" },
        { to: "/payments", text: "Payments" }
      );
    } else if (user?.role === 'STAFF') {
      links.push(
        { to: "/staff", text: "Staff Dashboard" },
        { to: "/memberships", text: "Memberships" },
        { to: "/equipment", text: "Equipment" },
        { to: "/gym-classes", text: "Gym Classes" },
        { to: "/payments", text: "Payments" }
      );
    } else if (user?.role === 'TRAINER') {
      links.push(
        { to: "/trainer", text: "Trainer Dashboard" },
        { to: "/training-sessions", text: "Training Sessions" },
        { to: "/class-registrations", text: "Class Registrations" }
      );
    } else if (user?.role === 'MEMBER') {
      links.push(
        { to: "/member", text: "Member Dashboard" },
        { to: "/training-sessions", text: "Training Sessions" },
        { to: "/gym-classes", text: "Gym Classes" },
        { to: "/class-registrations", text: "Class Registrations" }
      );
    }

    return links.map((link, index) => (
      <Nav.Link 
        key={index} 
        as={Link} 
        to={link.to}
        className={location.pathname === link.to ? 'active' : ''}
      >
        {link.text}
      </Nav.Link>
    ));
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg" className="mb-4">
      <Container>
        <Navbar.Brand as={Link} to="/">üèãÔ∏è Gym Management System</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            {getNavLinks()}
          </Nav>
          <Nav>
            {isAuthenticated ? (
              <>
                <Navbar.Text className="me-3">
                  Welcome, {user?.firstName} {user?.lastName} 
                  <span className={`badge ms-2 role-badge-${user?.role?.toLowerCase()}`}>
                    {user?.role}
                  </span>
                </Navbar.Text>
                <Button variant="outline-light" onClick={handleLogout}>
                  Logout
                </Button>
              </>
            ) : (
              <Nav.Link as={Link} to="/login">Login</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigation;
