import React from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Navbar, Nav, Container, Button, Card, Row, Col } from 'react-bootstrap';

const Home = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'ADMIN':
        return 'role-badge role-admin';
      case 'TRAINER':
        return 'role-badge role-trainer';
      case 'STAFF':
        return 'role-badge role-staff';
      case 'MEMBER':
        return 'role-badge role-member';
      default:
        return 'role-badge';
    }
  };

  return (
    <div>
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand href="/">üèãÔ∏è Gym Management System</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="/">Home</Nav.Link>
              {isAuthenticated && (
                <>
                  <Nav.Link href="/profile">Profile</Nav.Link>
                  {user?.role === 'MEMBER' && (
                    <Nav.Link href="/member">Member Dashboard</Nav.Link>
                  )}
                  {user?.role === 'TRAINER' && (
                    <Nav.Link href="/trainer">Trainer Dashboard</Nav.Link>
                  )}
                  {user?.role === 'STAFF' && (
                    <Nav.Link href="/staff">Staff Dashboard</Nav.Link>
                  )}
                  {user?.role === 'ADMIN' && (
                    <Nav.Link href="/admin">Admin Dashboard</Nav.Link>
                  )}
                </>
              )}
            </Nav>
            <Nav>
              {isAuthenticated ? (
                <>
                  <span className="navbar-text me-3">
                    Welcome, {user?.firstName} {user?.lastName}
                    <span className={`ms-2 ${getRoleBadgeClass(user?.role)}`}>
                      {user?.role}
                    </span>
                  </span>
                  <Button variant="outline-light" onClick={handleLogout}>
                    Logout
                  </Button>
                </>
              ) : (
                <>
                  <Nav.Link href="/login">Login</Nav.Link>
                  <Nav.Link href="/register">Register</Nav.Link>
                </>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <div className="welcome-section">
        <Container>
          <h1>Welcome to Gym Management System</h1>
          <p>A comprehensive solution for managing gym operations with role-based access control</p>
        </Container>
      </div>

      <Container>
        {isAuthenticated ? (
          <div>
            <h2 className="mb-4">Dashboard</h2>
            <Row>
              <Col md={3}>
                <Card className="dashboard-card h-100">
                  <Card.Body className="text-center">
                    <h5>Profile</h5>
                    <p>View and update your profile information</p>
                    <Button variant="primary" onClick={() => navigate('/profile')}>
                      View Profile
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
              
              {user?.role === 'MEMBER' && (
                <Col md={3}>
                  <Card className="dashboard-card h-100">
                    <Card.Body className="text-center">
                      <h5>Member Dashboard</h5>
                      <p>Access member-specific features and services</p>
                      <Button variant="success" onClick={() => navigate('/member')}>
                        Member Area
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
              
              {user?.role === 'TRAINER' && (
                <Col md={3}>
                  <Card className="dashboard-card h-100">
                    <Card.Body className="text-center">
                      <h5>Trainer Dashboard</h5>
                      <p>Manage training sessions and member progress</p>
                      <Button variant="success" onClick={() => navigate('/trainer')}>
                        Trainer Area
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
              
              {user?.role === 'STAFF' && (
                <Col md={3}>
                  <Card className="dashboard-card h-100">
                    <Card.Body className="text-center">
                      <h5>Staff Dashboard</h5>
                      <p>Handle gym operations and member services</p>
                      <Button variant="warning" onClick={() => navigate('/staff')}>
                        Staff Area
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
              
              {user?.role === 'ADMIN' && (
                <Col md={3}>
                  <Card className="dashboard-card h-100">
                    <Card.Body className="text-center">
                      <h5>Admin Dashboard</h5>
                      <p>Full system administration and management</p>
                      <Button variant="danger" onClick={() => navigate('/admin')}>
                        Admin Area
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
            </Row>
          </div>
        ) : (
          <div className="text-center">
            <h2>Get Started</h2>
            <p className="lead">Please login or register to access the gym management system.</p>
            <div className="mt-4">
              <Button variant="primary" size="lg" className="me-3" onClick={() => navigate('/login')}>
                Login
              </Button>
              <Button variant="outline-primary" size="lg" onClick={() => navigate('/register')}>
                Register
              </Button>
            </div>
          </div>
        )}
      </Container>
    </div>
  );
};

export default Home;
