import React, { useState, useEffect } from 'react';
import { gymClassAPI } from '../services/api'; 

const BookTrainingSession = () => {
  const [trainers, setTrainers] = useState([]);
  const [activeClasses, setActiveClasses] = useState([]);
  const [form, setForm] = useState({
    trainerId: '',
    memberId: 0,
    type: '',
    scheduledDate: '',
    duration: '',
    price: '',
    notes: '',
    location: ''
  });

  const token = localStorage.getItem('token');

useEffect(() => {
  userAPI.getAll()
    .then(res => {
      // Assuming res.data is an array of all users,
      // filter only trainers by role or some property:
      const trainersOnly = res.data.filter(user => user.role === 'TRAINER');
      setTrainers(trainersOnly);
    })
    .catch(err => console.error('Error fetching trainers:', err));
}, []);


  const handleChange = async (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));

    if (name === 'trainerId' && value) {
      try {
        const res = await gymClassAPI.getActiveByTrainer(value);
        setActiveClasses(res.data);
      } catch (err) {
        console.error('Error fetching active gym classes:', err);
        setActiveClasses([]);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch('/api/training-sessions/book', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(form)
      });
      alert('Session booked successfully');
    } catch (err) {
      alert('Error: ' + (err.response?.data?.message || 'Booking failed'));
    }
  };

  const sessionTypes = [
    'PERSONAL_TRAINING',
    'GROUP_TRAINING',
    'CONSULTATION',
    'ASSESSMENT',
    'NUTRITION_COUNSELING'
  ];

  return (
    <form onSubmit={handleSubmit}>
      <select name="trainerId" onChange={handleChange} required>
        <option value="">Select Trainer</option>
        {trainers.map(t => (
          <option key={t.id} value={t.id}>{t.fullName}</option>
        ))}
      </select>

      <input
        type="datetime-local"
        name="scheduledDate"
        onChange={handleChange}
        required
      />

      <select name="type" onChange={handleChange} required>
        <option value="">Select Session Type</option>
        {sessionTypes.map(type => (
          <option key={type} value={type}>{type.replace(/_/g, ' ')}</option>
        ))}
      </select>

      <input
        type="number"
        name="duration"
        placeholder="Duration (min)"
        onChange={handleChange}
        required
      />

      <input
        type="number"
        name="price"
        placeholder="Price"
        onChange={handleChange}
        required
      />

      <input
        type="text"
        name="location"
        placeholder="Location"
        onChange={handleChange}
        required
      />

      <textarea
        name="notes"
        placeholder="Notes"
        onChange={handleChange}
      ></textarea>

      <button type="submit">Book</button>

      {/* Optional: Display fetched active gym classes */}
      {activeClasses.length > 0 && (
        <div>
          <h4>Active Gym Classes for Selected Trainer:</h4>
          <ul>
            {activeClasses.map(c => (
              <li key={c.id}>
                {c.name} â€” {new Date(c.startTime).toLocaleString()}
              </li>
            ))}
          </ul>
        </div>
      )}
    </form>
  );
};

export default BookTrainingSession;
